    /* 
    * exepcion para que no se repita o no ingresar dato invalida
    */
    private void validarPelicula (){
     
        if(this.tfPeliculas.getText().isBlank() || this.tfPeliculas.getText().isEmpty())  
        return;
        
        int n =  this.cbPeliculas.getItemCount();
        boolean isFound = false;
        
        for(int i = 0; i<n; i++){
            if (this.tfPeliculas.getText().equals(this.cbPeliculas.getItemAt(i))) {
                isFound= !false;
                break;
            }
        }
        
        if (!isFound) {
            this.cbPeliculas.addItem(this.tfPeliculas.getText());
        } 
        else
        {
            JOptionPane.showMessageDialog(this, "Nombre ya en existencia", "Error en nombre de pelicula", JOptionPane.WARNING_MESSAGE);
        }
        
    }
    /*
    *
    *   Eliminar un elemento seleccionado de la ComboBox
    *
    */
    
    private void eliminarPelicula (){
        int idPelicula = cbPeliculas.getSelectedIndex();
        cbPeliculas.removeItemAt(idPelicula);
    }



